INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (1, 'de', 'A few decades after the destruction of the Inca Empire, a Spanish expedition led by the infamous Aguirre leaves the mountains of Peru and goes down the Amazon River in search of the lost city of El Dorado. When great difficulties arise, Aguirre’s men start to wonder whether their quest will lead them to prosperity or certain death.', 16.16, '1972-12-29', 'Aguirre, the Wrath of God', 7.522);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (2, 'en', 'A New York nightclub manager tries to save his brother and father from Russian mafia hitmen.', 16.132, '2007-10-12', 'We Own the Night', 6.7);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (3, 'fr', 'Secrets, rumors and betrayals surround the upcoming marriage between a young dissolute man and virtuous woman of the French aristocracy.', 7.62, '2007-05-30', 'The Last Mistress', 5.9);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (4, 'fr', 'A man rescues a woman from a suicide attempt in a gay nightclub. Walking the streets together, she propositions him: She''ll pay him to visit her at her isolated house for four consecutive nights. There he will silently watch her. He''s reluctant, but agrees. As the four nights progress, they become more intimate with each other, and a mutual fascination/revulsion develops. By the end of the four-day "contract", these two total strangers will have had a profound impact on each other.', 22.176, '2004-01-28', 'Anatomy of Hell', 4.7);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (5, 'de', 'In Hamburg, Ibrahim "Ibo" Secmez, of Turkish descent, wants to direct the first German kung-fu movie. For now, he makes commercials for his uncle''s kebab restaurant. Titzie, an aspiring actress and Ibo''s German girlfriend, finds she''s pregnant. Ibo is uncertain about fatherhood - compounded by his father''s disowning him for getting a German girl pregnant - so Titzie sends him packing. He makes attempts at getting it right, but as the birth approaches, he''s still not ready. In the background are three thugs in search of good tripe soup and a Capulet-Montegue feud between the kebab joint and a Greek taverna across the street. Can Ibo be the glove upon that hand?', 4.983, '2004-04-21', 'Kebab Connection', 6.6);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (6, 'en', 'A Reno singer witnesses a mob murder and the cops stash her in a nunnery to protect her from the mob''s hitmen. The mother superior does not trust her, and takes steps to limit her influence on the other nuns. Eventually the singer rescues the failing choir and begins helping with community projects, which gets her an interview on TV—and identification by the mob.', 19.35, '1992-05-28', 'Sister Act', 6.812);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (7, 'en', 'A modern-day witch likes her neighbor but despises his fiancée, so she enchants him to love her instead... only to fall in love with him for real.', 12.797, '1958-12-25', 'Bell, Book and Candle', 6.5);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (8, 'en', 'Augusto and Michaela Odone are dealt a cruel blow by fate when their five-year-old son Lorenzo is diagnosed with a rare and incurable disease. But the Odones'' persistence and faith leads to an unorthodox cure which saves their boy and re-writes medical history.', 14.567, '1992-12-30', 'Lorenzo''s Oil', 7.1);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (9, 'ro', 'Two college roommates have 24 hours to make the ultimate choice as they finalize arrangements for a black market abortion.', 12.793, '2007-08-24', '4 Months, 3 Weeks and 2 Days', 7.5);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (10, 'ja', 'A young woman working at a retirement home takes an elderly man living there on an excursion into the countryside, but the two wind up stranded in the titular forest.', 6.504, '2007-05-29', 'The Mourning Forest', 6.8);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (11, 'fr', 'In 1970s Iran, Marjane ''Marji'' Statrapi watches events through her young eyes and her idealistic family of a long dream being fulfilled of the hated Shah''s defeat in the Iranian Revolution of 1979. However as Marji grows up, she witnesses first hand how the new Iran, now ruled by Islamic fundamentalists, has become a repressive tyranny on its own.', 15.764, '2007-06-27', 'Persepolis', 7.9);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (12, 'de', 'Johan and his family are Mennonites from the north of Mexico. Against the law of God and Man, Johan falls in love with another woman.', 7.187, '2007-10-02', 'Silent Light', 7);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (13, 'fr', 'The true story of Elle France editor Jean-Dominique Bauby, who, in 1995 at the age of 43, suffered a stroke that paralyzed his entire body, except his left eye. Using that eye to blink out his memoir, Bauby eloquently described the aspects of his interior world, from the psychological torment of being trapped inside his body to his imagined stories from lands he''d only visited in his mind.', 11.758, '2007-05-23', 'The Diving Bell and the Butterfly', 7.558);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (14, 'de', 'The lives of six German-Turkish immigrants are drawn together by circumstance: An old man and a prostitute forging a partnership, a young scholar reconciling his past, two young women falling in love, and a mother putting the shattered pieces of her life back together.', 10.431, '2007-09-27', 'The Edge of Heaven', 7.2);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (15, 'ko', 'Shin-ae moves to her recently late husband’s hometown. Despite her efforts to settle in this unfamiliar and too-normal place, she finds that she can’t fit in. After a sudden tragedy, Shin-ae turns to Christianity to relieve her pain, but when even this is not permitted, she wages a war against God.', 11.057, '2007-05-17', 'Secret Sunshine', 7.1);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (16, 'fr', '1943. They have never stepped foot on French soil but because France was at war, Said, Abdelkader, Messaoud and Yassir enlist in the French Army, along with 130,000 other “indigenous” soldiers, to liberate the “fatherland” from the Nazi enemy. Heroes that history has forgotten…', 10.852, '2006-09-27', 'Days of Glory', 6.7);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (17, 'en', 'While doing a friend a favor and searching for a runaway teenager, a police detective stumbles upon a bizarre band of criminals about to pull off a bank robbery.', 14.375, '2002-06-28', 'No Good Deed', 5.4);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (18, 'en', 'San Francisco''s premiere wedding planner, Mary Fiore is rescued from an accident by the man of her dreams, paediatrician Steve Edison, only to find he is the fiancé of her latest client. As Mary continues making the wedding arrangements, she and Steve are put into a string of uncomfortable situations that force them to face their mutual attraction.', 16.661, '2001-01-26', 'The Wedding Planner', 5.9);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (19, 'en', 'When a woman''s father goes missing, she enlists a local to aid in her search.  The pair soon discover that her father has died at the hands of a wealthy sportsman who hunts homeless men as a form of recreation.', 38.2, '1993-04-27', 'Hard Target', 6.3);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (20, 'en', 'Jimmie is seeing his single friends get married one by one. He isn''t too worried until his girlfriend Anne catches the bouquet at his friend Marco''s wedding. Suddenly, his wild mustang days are numbered. He finally decides to propose to her, but he sticks his foot in his mouth and botches the proposal. Being insulted by the defeatist proposal, Anne leaves town on an assignment. After she''s gone, he finds out that his recently-deceased grandfather''s will stipulates that he gets nothing of a multi-million dollar fortune unless he''s married by 6:05pm on his 30th birthday: tomorrow! Not being able to find Anne, Jimmie begins backtracking through his past girlfriends to find a wife.', 12.496, '1999-11-05', 'The Bachelor', 5.2);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (21, 'en', 'Jack always lands on his feet. He lands on his feet when he marries the beautiful Sarah. He lands on his feet when he buys a luxurious new home. However, when Sarah goes into labour, he takes a tumble down the stairs and lands on his head. When he comes around he discovers he is the proud father of a baby girl, but deficient in the spouse department to the tune of 1.', 5.72, '1995-06-02', 'Jack & Sarah', 6.8);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (22, 'en', 'When Longfellow Deeds, a small-town pizzeria owner and poet, inherits $40 billion from his deceased uncle, he quickly begins rolling in a different kind of dough. Moving to the big city, Deeds finds himself besieged by opportunists all gunning for their piece of the pie. Babe, a television tabloid reporter, poses as an innocent small-town girl to do an exposé on Deeds.', 22.329, '2002-06-28', 'Mr. Deeds', 5.837);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (23, 'en', 'Set in 1890, this is the story of a Pony Express courier who travels to Arabia to compete with his horse, Hidalgo, in a dangerous race for a massive contest prize, in an adventure that sends the pair around the world...', 21.627, '2004-02-04', 'Hidalgo', 6.81);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (24, 'en', 'After proving himself on the field of battle in the French and Indian War, Benjamin Martin wants nothing more to do with such things, preferring the simple life of a farmer. But when his son Gabriel enlists in the army to defend their new nation, America, against the British, Benjamin reluctantly returns to his old life to protect his son.', 37.635, '2000-06-28', 'The Patriot', 7.161);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (25, 'fr', 'Martial is discharged from a mental insitution where he spent a few years due to a serious nervous breakdown.  Upon his return he finds his mother, a busy business woman who owns a supermarket chain, convinced that her son will be able to find himself again if tasked with some responsibilities. Soon enough, he''s sent to Limoges on a business trip to check on one of their stores in the hope to reinvigorate the failing business. Once he arrives, Martial is faced with responsibilities he had never imagined, including dealing with the store''s personnel.', 6.54, '1988-08-14', 'A Few Days with Me', 5.8);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (26, 'en', 'When a mafia accountant is taken hostage on his beat, a police officer – wracked by guilt from a prior stint as a negotiator – must negotiate the standoff, even as his own family is held captive by the mob.', 16.885, '2005-03-10', 'Hostage', 6.3);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (27, 'en', 'Lloyd, an eternal optimist, seeks to capture the heart of Diane, an unattainable high school beauty and straight-A student. He surprises just about everyone-including himself-when she returns the sentiment. But Diane''s over-possessive, divorced Dad disapproves and it''s going to take more than just the power of love to conquer all.', 13.727, '1989-04-14', 'Say Anything...', 7.1);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (28, 'fr', 'Tanguy is 28 years old and still living with his parents. They think it''s time he moves out. He doesn''t, so they hatch a plan.', 6.304, '2001-11-21', 'Tanguy', 5.9);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (29, 'de', 'Thomas Mann''s witty and intimate story of a irresistible scoundrel.', 1.96, '1957-04-24', 'Confessions of Felix Krull', 7.2);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (30, 'de', 'Young Prince Nechljudov is summoned as a judge in a murder trial. A rich merchant was found dead in the room of the inn where he was staying and the prostitute Maslova was accused of the crime. Nechljudov recognizes in the woman the maid of the aunts he had seduced and abandoned years before and tries to convince the authorities of her of his innocence but to no avail. Convinced that he is responsible for her moral fall, he follows her to Siberia where she must serve her sentence.', 1.846, '1958-10-21', 'Resurrection', 6);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (31, 'ru', 'A group of American pilots from Alaska ferry Airacobra fighter planes across the ocean on Lend-lease. The orderly course of life is disrupted when it becomes clear that the American pilots are attractive and charming young women. The feelings of the Russian young men collide into barriers of culture and language resulting in a host of awkward, funny, and sometimes tragic situations.It is the story of Russians, Americans, and natives of the Far North. It is the story of man and woman in war. Love and death are squeezed between the hills as human fates are destroyed and born.', 1.344, '2006-06-04', 'Transit', 4.8);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (32, 'en', 'Story of the early life of genius and Nobel Prize-winning physicist Richard Feynman.', 5.333, '1996-10-04', 'Infinity', 6.6);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (33, 'en', 'On his first day on the job as a narcotics officer, a rookie cop works with a rogue detective who isn''t what he appears.', 32.308, '2001-10-05', 'Training Day', 7.477);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (34, 'en', 'Martin Sheen stars as an American newsman in Rome who begins to investigate the appearance of several corpses found throughout Europe with their hands cut off. He soon uncovers not only plots of plutonium theft, but also of nuclear arms deals and dark political schemes.', 3.923, '1991-01-01', 'Touch and Die', 3);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (35, 'en', 'CIA agent Harry Wargrave is sent to aid Gen. Marenkov, a senior Russian official, who is defecting to the west. Wargrave decides they should travel to safety on a train across Europe, the "Atlantic Express". During the journey, they must survive attacks by terrorists and an avalanche, all planned by Russian spy-catcher Nikolai Bunin.', 4.115, '1979-09-27', 'Avalanche Express', 4.5);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (36, 'fr', 'Harry is a young millionaire on holiday; he takes his yacht to a Greek island, and stays in the mansion of his friend...', 4.108, '1982-01-01', 'Aphrodite', 2.9);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (37, 'en', 'Chappy discovers a drug-smuggling scheme at his own air base. It turns out that the lives of some village people in Peru are at stake, and he decides to fly there with ancient airplanes and friends to free them.', 8.821, '1992-01-09', 'Iron Eagle III', 4);
INSERT INTO public.movie (movie_id, language, overview, popularity, release_date, title, vote_average) VALUES (38, 'en', 'No sooner does Italian-American widow Loretta accept a marriage proposal from her doltish boyfriend, Johnny, than she finds herself falling for his younger brother, Ronny. She tries to resist, but Ronny lost his hand in an accident he blames on his brother, and has no scruples about aggressively pursuing her while Johnny is out of the country. As Loretta falls deeper in love, she comes to learn that she''s not the only one in her family with a secret romance.', 18.855, '1987-12-16', 'Moonstruck', 6.846);
